import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Button } from 'react-bootstrap';

function App() {
  const [positions, setPositions] = useState({
    'Miss Scarlet': 'start',
    'Prof. Plum': 'start',
    'Mrs. Peacock': 'start',
    'Mr. Green': 'start',
    'Mrs. White': 'start',
    'Col. Mustard': 'start',
  });

  const rooms = [
    'Study', 'Hall', 'Lounge',
    'Library', 'Billiard Room', 'Dining Room',
    'Conservatory', 'Ballroom', 'Kitchen'
  ];

  const moveCharacter = async (character, room) => {
    try {
      const response = await axios.post('YOUR_BACKEND_ENDPOINT', {
        character,
        room
      });
      console.log(response.data); // 输出从后端返回的数据

      if (response.status === 200) {
        setPositions(prev => ({ ...prev, [character]: room }));
      }
    } catch (error) {
      console.error("Error moving character:", error);
    }
  }

  return (
    <Container>
      <Row>
        <Col md={7}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '10px' }}>
            {rooms.map(room => (
              <div key={room} style={{ border: '1px solid black', padding: '20px' }}>
                {room}
                <br />
                {Object.entries(positions).map(([character, position]) => 
                  position === room ? <span key={character}>{character}<br /></span> : null
                )}
              </div>
            ))}
          </div>
        </Col>
        <Col md={5}>
          <div style={{ backgroundColor: '#f5f5f5', padding: '20px', border: '1px solid black' }}>
            Info Panel
            {Object.keys(positions).map(character => (
              <div key={character} style={{ marginBottom: '10px' }}>
                {character}
                <Button 
                  style={{ marginLeft: '10px', backgroundColor: '#007bff', borderColor: '#007bff' }} 
                  onClick={() => moveCharacter(character, 'Study')}
                >
                  Move to Study
                </Button>
              </div>
            ))}
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
